sub _get_static_arg {
    my ( $pkg, $arg ) = @_;

    if ( !$pkg && !$arg ) {
	$arg = '<empty>';
    }
    elsif ( !$arg ) {
	if ( UNIVERSAL::isa( $pkg, 'Activator::Log' ) ) {
	    $arg = '<empty>';
	}
	else {
	    $arg = $pkg;
	}
    }
    chomp $arg;
    return $arg;
}
