sub dump_names {
  my($it, %o) = @_;

  my @out = ();
  $o{_depth} ||= 0;
  $o{spacer}   ||= sub {return "z"};
  $o{trunk}    ||= sub {return "|"};
  $o{branch}   ||= sub {return "="};
  $o{node}     ||= sub {return "O "};
  $o{break}    ||= "\n";
  $o{callback}   = sub {
      my($this, $o) = @_[0,1];

			unless($this->state){                      
					$this->pregnant(1) if $this->descendants; 
					$this->clear_daughters;                
			}

      my @spacer;
			my @ancestors = reverse $this->ancestors;

			my $index = 0;
			foreach my $ancestor (@ancestors){

					if($ancestor->ancestors == $this->ancestors -1){
							push @spacer, $o->{branch}->($this);
					}

					else {
							push @spacer, $ancestors[$index+1]->right_sister ? 
								$o->{trunk}->() : 
								$o->{spacer}->();
					}
					$index++;
			}

      push(@out,join('',
             @spacer,
             $o->{node}->($this),
						 $this->link->($this),
						 $o->{break},
      ));

      return 1;
    }
  ;
  $it->walk_down(\%o);
  return @out;
}
