sub set_curs {
  my ($w, $cbdata) = @_;

  my (undef, $x, $y, undef) = $w->window->get_pointer();
  ($x, $y) = $w->window_to_buffer_coords('widget', $x, $y);
  my $iter = $w->get_iter_at_location ($x, $y);
  return 0 unless $iter;
  my $curshand = defined(get_hlink($iter));
  if( !defined($cbdata->{CURS_HAND}) || $curshand != $cbdata->{CURS_HAND} ) {
      $w->get_window('text')->set_cursor
      		(Gtk2::Gdk::Cursor->new($curshand ? 'hand2' : 'xterm'));
    $cbdata->{CURS_HAND} =  $curshand;
  }
  return 0;
}

sub attr_bool {
  my ($id, $name, $val) = @_;
  my $cval = $val ? 'true' : 'false';
  my $line = "<dia:boolean val=\"$cval\"/>";
  dia_attr($id, $name, $line);
}

sub construct_tree {
  my ($nodes, $level, $min_off, $max_off, $levels, $tree_desc) = @_;
  foreach my $node (@$nodes) {
    prepare_node($node, $level, $min_off, $max_off, $levels, $tree_desc);
  }
}

sub ctree_kpress {
  my ($w, $event, $ud) = @_;
  my $key = Gtk2::Gdk->keyval_name($event->keyval());
  if( $key eq 'f' ) {
    $ud->{REGEXP} = 0;
  }
  elsif( $key eq 'r' ) {
    $ud->{REGEXP} = 1;
  }
  else {
    return 0;
  }
  my $st = $event->state();
  if( $#$st == 0 && $st->[0] eq 'control-mask' ) {
    $w->signal_emit('start_interactive_search');
    return 1;
  }
  return 0;
}
