sub get_words {
    my $text = shift;
    warn "get_swords : $text \n";
    my @words = split(/[^a-z0-9\xc0-\xff\+\_\-]+/, lc $text);
    warn "words : ", @words, "\n";
    @words = grep { s/[^a-z0-9\xc0-\xff\+\_\-]+//; $_ } grep { length > 1 } grep { /[a-z0-9]/ } @words;
    warn "words : ", @words, "\n";
