sub greek2num ($;$) {
	my($n,$ret,$thousands,$digit) = $_[0];

	$n =~ s/^\s+//;

	while (length $n) {
		$thousands = $n =~ s/^([͵,]+)// && length $1;
		if($n =~ s/^στ//i) {
			$digit = 6;
		}
		elsif(exists $greek_digit_2_num{substr $n,0,1}) {
			$digit = $greek_digit_2_num{substr $n,0,1,''};
		}
		else {
			$n =~ s/^['’ʹ´΄]?\s*//; 
			length $n or last;      
			warnings::warnif(       
			    numeric =>
			    qq/Argument "$_[0]" isn't numeric in greek2num/
			);
			last;
		}
		$ret += $digit * 1000**$thousands;
	}
	$ret;
}
