sub pwstrengthcheck($){
	my $pw = shift;

	my $pwlength = length($pw);
	if($pwlength>5){
		$pwlength = 5;
	}

	my $numnumeric = $pw;
	$numnumeric =~ s/[^0-9]//g;
	my $numeric = ($pwlength - length($numnumeric));
	if($numeric>3){
		$numeric = 3;
	}

	my $symbols = $pw;
	$symbols =~ s/[^\W]//g;
	my $numsymbols = ($pwlength - length($symbols));
	if($numsymbols>3){
		$numsymbols = 3;
	}

	my $numupper = $pw;
	$numupper =~ s/[^A-Z]//g;
	my $upper = ($pwlength - length($numupper));
	if($upper>3){
		$upper = 3;
	}
	my $pwstrength = (($pwlength*10)-20) + ($numeric*10) + ($numsymbols*15) + ($upper*10);

	if($pwstrength < 0){
		$pwstrength = 0;
	}

	if($pwstrength > 100){
		$pwstrength = 100;
	}

	return $pwstrength;
}
