sub holidays_es {
    my ($self, %params) = @_;
    my $year = $params{'year'} or croak 'Missing year argument';

    my $holidays = $self->holidays(year => $year);
    my %hd = ();

    require DateTime;

    for my $month_day (keys %$holidays) {
        my ($month, $day) = unpack "A2A2", $month_day;
        $hd{ $holidays->{$month_day} } = DateTime->new(
            year  => $year,
            month => $month,
            day   => $day,
        );
    }

    return \%hd;
}
