sub end_log {
    my $s = shift;
    my ($ar) = @_;

    my %b   = %{${$ar}{fld}}; 
    my $f   = "%a %b %e %H:%M:%S %Y"; 
    my $fh1 = ${$ar}{fh_brf}; 
    my $fh2 = ${$ar}{fh_dtl}; 
    my $cns = ${$ar}{cns}; 

    $b{end_time}     = time;
    $b{elapsed_time} = $b{end_time} - $b{start_time};
    $b{start_time}   = strftime $f, localtime($b{start_time});
    $b{end_time}     = strftime $f, localtime($b{end_time});
    $b{result}       = 'OK'; 

    my ($txt) = ("");
    foreach my $k (split /,/, $cns) { $txt .= "$b{$k}|"; }
    $txt =~ s/\|$//; 

    $self->disp_param(\%b); 
    print $fh1 $txt;  
    print $fh2 "# End at $b{end_time} $b{result}\n"; 
    undef $fh1;   
    undef $fh2;   
}
