sub satisfies {
    my( $self, $dep ) = @_;

    $dep = Debian::Dependency->new($dep)
        unless ref($dep);

    if( $self->alternatives ) {
        for( @{ $self->alternatives } ) {
            return 0 unless $_->satisfies($dep);
        }

        return 1;
    }

    if( $dep->alternatives ) {
        for( @{ $dep->alternatives } ) {
            return 1 if $self->satisfies($_);
        }

        return 0;
    }

    return 0 unless $self->pkg eq $dep->pkg;

    return 1 unless $dep->rel;

    return 0 if not $self->rel;

    my $cmpver = ( $self->ver <=> $dep->ver );

    if( $self->rel eq '>>' ) {
        return 1 if $dep->rel eq '>>'
            and $cmpver >= 0;

        return 1 if $dep->rel eq '>='
            and $cmpver >= 0;

        return 0;
    }
    elsif( $self->rel eq '>=' ) {
        return 1 if $dep->rel eq '>='
            and $cmpver >= 0;

        return 1 if $dep->rel eq '>>'
            and $cmpver > 0;

    }
    elsif( $self->rel eq '=' ) {
        return 1 if $dep->rel eq '='
            and $cmpver == 0;

        return 1 if $dep->rel eq '>='
            and $cmpver >= 0;

        return 1 if $dep->rel eq '>>'
            and $cmpver > 0;

        return 1 if $dep->rel eq '<='
            and $cmpver <= 0;

        return 1 if $dep->rel eq '<<'
            and $cmpver < 0;

        return 0;
    }
    elsif( $self->rel eq '<=' ) {
        return 1 if $dep->rel eq '<='
            and $cmpver <= 0;

        return 1 if $dep->rel eq '<<'
            and $cmpver < 0;

        return 0;
    }
    elsif( $self->rel eq '<<' ) {
        return 1 if $dep->rel eq '<<'
            and $cmpver <= 0;

        return 1 if $dep->rel eq '<='
            and $cmpver <= 0;

        return 0;
    }
    else {
        croak "Should not happen: $self satisfies $dep?";
    }
}
