	( my $sub = $AUTOLOAD ) =~ s/^Dwimmer::Client:://;
	my ( $self, %attr ) = @_;

	my $m = $self->mech;
	if ( $GET{$sub} ) {
		my $params = join "&", map {"$_=$attr{$_}"} keys %attr;
		my $url = $self->host . "/_dwimmer/$GET{$sub}.json?$params";

		$m->get($url);
	} elsif ( $POST{$sub} ) {
