            $data->{'eq_id'}     = $1;
            $data->{'warn_type'} = $warn_type{$2};
            $data->{'warn_code'} = $2;
            $data->{'warn_num'}  = $3 * 1;
	    $now_code='';
        }
        elsif ( $line
            =~ /(\d{3}) N(\d{3}) E(\d{4}) (\d{3}) (\d{2}) ([0-9\-\+]{2}) RK.+/
