            $data->{'center_lat'}   = $2 / 10;
            $data->{'center_lng'}   = $3 / 10;
            $data->{'center_depth'} = $4 * 1;
            $data->{'magnitude'}    = $5 / 10;
            $data->{'shindo'}       = $shindo{$6};
            $data->{'shindo_code'}  = $6;
	    $now_code='';
        }
        elsif ( $line =~ /(\d{4}) N(\d{3}) E(\d{4}) (\d{3})/ ) {
            $data->{'center_code'}  = $1;
            $data->{'center_name'}  = $ippan_shinou_code{$1};
            $data->{'center_lat'}   = $2 / 10;
            $data->{'center_lng'}   = $3 / 10;
            $data->{'center_depth'} = $4 * 1;
	    $now_code='';
        }
        elsif ( $line =~ /([CP][APB]I) ([0-9\s]+)/ ) {
