sub cattail ($$) {
	my ($input, $handle) = @_;
	my @lines = (0);

    unless (ref \$input eq 'GLOB' or ref \$input eq 'REF') {
		open FILE, $input or return;
	}

	while (<FILE>) {
		$lines[$.] = tell FILE;
	}

	pop @lines;
	while (defined ($_ = pop @lines)) {
		seek FILE, $_, 0;
		print $handle scalar(<FILE>);
	}
	close FILE;

	return (1);
}
