{
  my($adr_start, $adr_end, $disass)=@_;
  my($binary, $diff);

  $binary="";

  if ($disass =~ m#^f[bi]?st(p)?[sl]? #)
  {
    if ($1)
    {
      $binary .= "\xdf\xc0";

      $adr_start += 2;
    }
  }
  elsif ($disass =~ m#^pop#)
  {
    $binary .= "\x83\xc4\x04";
    $adr_start += 3;
  }
  elsif ($disass =~ m#^(mov|or|and)#)
  {
  }
  else
  {
    warn "Unknown instruction '" . $disass . "', patching unsafe!";
    return undef;
  }


  if (($diff=$adr_end-$adr_start) >= 2)
  {
    $binary .= "\xeb" . pack("C", $diff-2);
