sub headings2text {
   my $html = shift;
   my $change = $html;

   HEADING: while( $html=~/<h(\d)[^<>]*>([^<>]+)<\/h\1>/i ){
      my($h,$text) = ($1,$2);

      my $text_altered;
      if($h == 1 ){
         $text_altered = uc($text);
         $change=~s/<h$h[^<>]*>$text<\/h$h>/\n$text_altered\n/sig;
         next HEADING;
      }

      else {
         $text_altered = lc($text);
         $text_altered=~tr/\b[a-z]/[A-Z]/;

         $change=~s/<h$h[^<>]*>$text<\/h$h>/\n$text_altered\n/sig;
         next HEADING;

      }



   }

   return $change;

}
