sub make_table {
	my $tsref = shift;
	open_table($tsref);
	foreach my $key (sort { 
		if ($tsref->{SORTBY} eq "value") {
			if ($tsref->{ORDER} eq 'asc') {
				${$tsref->{DATA}}{$a} cmp ${$tsref->{DATA}}{$b};
			} else { 
				${$tsref->{DATA}}{$b} cmp ${$tsref->{DATA}}{$a};
			}
		} else {
			if ($tsref->{ORDER} eq 'asc') {
				$a cmp $b;
			} else {
				$b cmp $a;
			}
		}
	} keys %{$tsref->{DATA}}) {
		open_row;
		open_cell;
		$HTML::HashTable::output .= $key;
		close_cell(0);
		recurse_through($tsref, ${$tsref->{DATA}}{$key});
		close_row;
	}
	close_table;
}
