sub form_tag 
{
	my ($fattr, $closed) = @_;
	die if $closed;

	my $default;

	return unless compile_time_gate($fattr);

	my $text_cb = sub {
		my ($attr, $closed) = @_;

		rbuf(sub {
			return 1 unless run_time_gate($attr);
			$attr->set(value => qpval($attr->get('name')));
		});
	};

	my $vals = {};

	my $radio_cb = sub {
		my ($attr, $closed) = @_;

		rbuf(sub {
			my $name = run_time_gate($attr);
			return 1 unless $name;
			my $value = $attr->get('value');
			if (qpval($name, $value)) {
				$attr->set(checked => undef);
			} else {
				$attr->set(checked => 0);
				$attr->hide('checked');
			}
			return 1;
		});
	};

	my $nothing = sub { 1 };

	my $input_cb = sub {
		my ($attr, $closed) = @_;

		return 1 unless compile_time_gate($attr);

		my %handlers = (
			text		=> $text_cb,
			password	=> $text_cb,
			radio		=> $radio_cb,
			checkbox	=> $radio_cb,
			submit		=> $nothing,
			hidden		=> $nothing,
			reset		=> $nothing,
			file		=> $nothing, 	
			image		=> $nothing,
			button		=> $nothing,
		);

		my $type = lc($attr->get('type'));
		die unless $handlers{$type};
		$handlers{$type}->($attr, $closed);
		$attr->eval_at_runtime(1);
		return 1;
	};

	my $textarea_cb = sub {
		my ($attr, $closed) = @_;
		return 1 unless compile_time_gate($attr);

		$attr->eval_at_runtime(1);
		my ($b, $deferred) = capture_compile('textarea', $attr, undef, %HTML::Transmorgify::queued_intercepts);

		my $b2 = [];
		{
			local($HTML::Transmorgify::rbuf) = $b2;
			for my $ccb (@HTML::Transmorgify::queued_captures) {
				$ccb->($b);
			}
		}

		postbuf(sub {
			my $name = run_time_gate($attr);

			if ($name) {
				$HTML::Transmorgify::result->[0] .= encode_entities(qpval($name)) . "</textarea>";
			} else {
				run($b);
				run($b2);
				$deferred->doit();
				$HTML::Transmorgify::result->[0] .= "</textarea>";
			}
		});
		return 1;
	};

	my $select_cb = sub {
		my ($attr, $closed) = @_;
		return 1 unless compile_time_gate($attr);
		$attr->eval_at_runtime(1);

		my $option_cb = sub {
			my ($oattr, $closed) = @_;
			return 1 unless compile_time_gate($attr);

			$oattr->eval_at_runtime(1);

			my $get_value;
			if (defined $oattr->raw('value')) {
				$get_value = sub {
					$oattr->get('value');
				};
			} else {
				my $b;
				queue_capture(sub {
					$b = shift;
				});
				$get_value = sub {
					local(@btmp) = ('');
					run($b, \@btmp);
					return $btmp[0];
				};
			}

			rbuf(sub {
				my $name = run_time_gate($attr);
				return 1 unless $name;

				my $value = $get_value->();

				if (qpval($name, $value)) {
					$oattr->set(selected => undef);
				} else {
					$oattr->set(selected => 0);
					$oattr->hide('selected');
				}
			});
		};

		queue_intercept(__PACKAGE__,
			option		=> $option_cb,
			"/select",	=> sub { 1 },
		);
	};


	queue_intercept(__PACKAGE__,
		input		=> $input_cb,
		textarea	=> $textarea_cb,
		select		=> $select_cb,
		'/form'		=> \&return_true,
	);
	return 1;
}

sub do_trim
{
	my ($attr, $buf) = @_;

	my $trim = $attr->raw('trim');
	if (@$buf && boolean($trim, 0)) {
		if ($trim eq 'all') {
			(s/^\s+// || s/\s+$//) 
				for grep { ! ref($_) } @$buf;
		} else {
			if ($trim ne 'end') {
				$buf->[0] =~ s/^\s+//
					unless ref $buf->[0];
			}
			if ($trim ne 'start') {
				$buf->[$#$buf] =~ s/\s+$//
					unless ref $buf->[$#$buf];
			}
		}
	}
}
