sub to_html {
    my $block = Kwiki::Formatter::Paragraph->new(
        hub => $self->hub, 
        text => $self->block_text,
    );
    $block->parse_phrases;
    my $html = $block->to_html;
    $html =~ s/.*?<p.*?>\n?(.*)<\/p>.*/$1/s;
    $html =~ s/\n/<br \/>\n/g;
    $html =~ s/^( +)/'&nbsp;' x length($1)/gem;
    return "<p>\n$html</p>";
}
