sub add_node {
	my ($self, $node, $rank) = @_;

	for(@{$node->{child_nodes}}){
		my $label = $_->{node_name_safe};
		$label =~ s/\\/\\\\/g;

		my $fillcolor = $_->{spring}?$self->{spring_col}:$self->{node_col};

		$self->{graph}->add_node(
			$_->{uid}, 
			label		=> $label,
			rank		=> $rank,
			fillcolor	=> $fillcolor,
			style		=> 'filled',
			fontname	=> $self->{fontname},
			fontsize	=> $self->{fontsize},
		);

		$self->{graph}->add_edge(
			$node->{uid} => $_->{uid},
			arrowtail => 'normal',
		) if ($node->{uid} != $self->{interp}->{root_node}->{uid});

		$self->add_node($_, $rank+1);
	}
}
