sub count_chars {
	my ( $input, $mode ) = validate_pos( @_, STRING,
	{
		%{+NUMBER_RANGE( 0, 4 )},
		optional => 1,
		default => 0
		},
	);

	if($mode < 3){
		use bytes;
		my %freq;
		my @freq;
		@freq{0..255} = (0) x 256 if $mode != 1;
		$freq{ord $_}++ for split //, $input;
		if($mode == 2){
			$freq{$_} and delete $freq{$_} for keys %freq;
		}
		return %freq;
	}else{
		my %freq = count_chars( $input, $mode-2 );
		return join '', map chr, sort keys %freq;
	}
}
