sub _open_socket_timeout {
	my $timeout = shift;
	my $s; 
	my $error;
	DEBUG( "_open_socket_timeout() ", join(',', @_));
	undef $!; undef $@;
	$@ = eval {
		$SIG{ALRM} = sub { die "connection TIMED OUT" };
		alarm $timeout;
		$s = IO::Socket::INET->new( @_ );
		alarm 0;
		$@ =~ s/IO::Socket::INET:\s+//;
		return $@;
	};

	undef $ERROR;
	if ($@ =~ /connection TIMED OUT/ ) {
			$ERROR = 'connection TIMED OUT' ;
			undef $s;
	}
	$ERROR ||= $@||$!  unless $s;
	DEBUG("\$s=$s -- \$!='$!' -- \$\@='$@'  -- \$ERROR=$ERROR");
	return $s;
}
