  my ($obj, @meths) = @_;
  if (want('LVALUE')) {
    if (my @rvals = want('ASSIGN')) {
      for my $i (0 .. $#meths) {
        my $meth = $meths[$i];
        $obj->$meth($rvals[$i]);
