sub database_item {
	my $self = shift;
	my ($request, $response) = @_;

	my ($intid) = ($request->uri->path =~ /^\/databases\/\d+\/items\/(\d+)/);
	my $dbh = $self->connect;
	my $sth = $dbh->prepare(
		'SELECT filename FROM videometadata WHERE intid = ?');
	$sth->execute($intid);

	my ($filename) = $sth->fetchrow_array;
	open $response->{handle}, '<', $filename;

	$response->streaming(1);
}
