sub Paw_main_loop {
    my $main_win = shift;
    $Paw::main_win = $main_win;

    my $i = '';
    $main_win->_refresh();
    while ( not $main_win->{close_it} and ($i ne $main_win->{quit_key}) ) {
        $i = getch();              
        &{$main_win->{time_function}} if defined $main_win->{time_function};
        if ( $i ne -1 ) {
            $main_win->key_press($i);  
            $main_win->_refresh();
        }
        else {
            $main_win->_refresh() if defined $main_win->{time_function};
        }
    };
    Curses::clear();
    $main_win->{close_it}=0;
    endwin();
}

sub _win_cb {
    my $this = shift;
    my $i;

    do {
	$i = getch();                  
	if ( $i eq ' ' ) {
	    $i = -1;
	}
	elsif ( $i eq "\n" ) {
	    return;
	}
	if ( $i ne -1 ) {
	    $this->key_press($i);      
	    $this->_refresh();
	}
    } while ( $i ne "\n" );
}
