sub uniq {
  my ($self, $filename_or_handle) = @_;

  my ($dev, $ino)
    = (ref $filename_or_handle && $filename_or_handle->can('stat')
       ? $filename_or_handle->stat
       : stat ($filename_or_handle));

  if (! defined $dev) {
    return 1;
  }

  my $key = "$dev,$ino";

  my $hash = $self->{'hash'};
  return (! exists $hash->{$key}
          && ($hash->{$key} = 1));
}


