sub slave
{
 my ($obj) = @_;
 my $slave = $obj->{'slave'};
 my $tty   = $obj->{'tty'};
 open($slave,"+>$tty") || croak "Cannot open $slave as $tty:$!";
 InitSlave($slave,$tty);
