sub execute($$$@) {
	my ($self, $soaph, $method, @args) = @_;

	$soaph->uri($self->{namespace});
	my $som = $soaph->call($method => @args);
	my $fc = $som->faultcode();
	if ($fc) {
		my $ex;
		if ($fc =~ /^[^:]*:Client/i) {
			$ex = ResourcePool::Command::NoFailoverException->new($som->fault());
		} else {
			$ex = $som->fault();
		}

		die $ex;
	}
	return $som->result();
}
