sub display {
	my $self = shift;

	OpenGL::glEnable( $self->{id} );
	OpenGL::glLightfv_p(
		$self->{id},
		OpenGL::GL_POSITION,
		@{$self->{position}},
	);
	OpenGL::glLightfv_p(
		$self->{id},
		OpenGL::GL_AMBIENT,
		@{$self->{ambient}},
	);
	OpenGL::glLightfv_p(
		$self->{id},
		OpenGL::GL_DIFFUSE,
		@{$self->{diffuse}},
	);
	OpenGL::glLightfv_p(
		$self->{id},
		OpenGL::GL_SPECULAR,
		@{$self->{specular}},
	);
}

sub move {
	my $self = shift;
	my $step = shift;

	$self->{position}->[0] += $self->{velocity}->[0] * $step;
	$self->{position}->[1] += $self->{velocity}->[1] * $step;
	$self->{position}->[2] += $self->{velocity}->[2] * $step;

	if ( $self->{orient} and $self->{rotate} ) {
		$self->{orient}->[0] += $self->{rotate} * $step;
	}

	return;
}

sub compile {
	my $self   = shift;

	glDisable( GL_LIGHTING );
	glEnable( GL_TEXTURE_2D );
	glColor4f( 1, 1, 1, 1 );


	$self->{north}->display;
	glBegin( GL_QUADS );
	glTexCoord2f( 0, 0 ); glVertex3f( -1.002,  1.002, -1 ); 
	glTexCoord2f( 0, 1 ); glVertex3f( -1.002, -1.002, -1 ); 
	glTexCoord2f( 1, 1 ); glVertex3f(  1.002, -1.002, -1 ); 
	glTexCoord2f( 1, 0 ); glVertex3f(  1.002,  1.002, -1 ); 
	glEnd();

	$self->{south}->display;
	glBegin( GL_QUADS );
	glTexCoord2f( 0, 0 ); glVertex3f(  1.002,  1.002,  1 ); 
	glTexCoord2f( 0, 1 ); glVertex3f(  1.002, -1.002,  1 ); 
	glTexCoord2f( 1, 1 ); glVertex3f( -1.002, -1.002,  1 ); 
	glTexCoord2f( 1, 0 ); glVertex3f( -1.002,  1.002,  1 ); 
	glEnd();

	$self->{east}->display;
	glBegin( GL_QUADS );
	glTexCoord2f( 0, 0 ); glVertex3f(  1,  1.002, -1.002 ); 
	glTexCoord2f( 0, 1 ); glVertex3f(  1, -1.002, -1.002 ); 
	glTexCoord2f( 1, 1 ); glVertex3f(  1, -1.002,  1.002 ); 
	glTexCoord2f( 1, 0 ); glVertex3f(  1,  1.002,  1.002 ); 
	glEnd();

	$self->{west}->display;
	glBegin( GL_QUADS );
	glTexCoord2f( 0, 0 ); glVertex3f( -1,  1.002,  1.002 ); 
	glTexCoord2f( 0, 1 ); glVertex3f( -1, -1.002,  1.002 ); 
	glTexCoord2f( 1, 1 ); glVertex3f( -1, -1.002, -1.002 ); 
	glTexCoord2f( 1, 0 ); glVertex3f( -1,  1.002, -1.002 ); 
	glEnd();

	$self->{up}->display;
	glBegin( GL_QUADS );
	glTexCoord2f( 0, 0 ); glVertex3f( -1.002,  1,  1.002 ); 
	glTexCoord2f( 0, 1 ); glVertex3f( -1.002,  1, -1.002 ); 
	glTexCoord2f( 1, 1 ); glVertex3f(  1.002,  1, -1.002 ); 
	glTexCoord2f( 1, 0 ); glVertex3f(  1.002,  1,  1.002 ); 
	glEnd();

	if ( $self->{down} ) {
		$self->{down}->display;
		glBegin( GL_QUADS );
		glTexCoord2f( 0, 0 ); glVertex3f( -1.002, -1,  1.002 ); 
		glTexCoord2f( 0, 1 ); glVertex3f( -1.002, -1, -1.002 ); 
		glTexCoord2f( 1, 1 ); glVertex3f(  1.002, -1, -1.002 ); 
		glTexCoord2f( 1, 0 ); glVertex3f(  1.002, -1,  1.002 ); 
		glEnd();
	}

	glClear( GL_DEPTH_BUFFER_BIT );

	glEnable( GL_LIGHTING );

	return 1;
}
