sub rtttl_to_midi {
 my $parser = shift; 
 my $patch = shift; 
 unless(defined($patch)) {
  $patch = 1; 
 }
 my $ms = MIDI::Simple->new_score();
 if (length($parser->get_part_name())) {
  $ms->track_name($parser->get_part_name());
 }
 $ms->patch_change(0,$patch-1); 
 $ms->set_tempo(60000000  / $parser->get_bpm()); 
 $ms->noop('fff','c0'); 
 foreach my $nref ($parser->get_notes()) {
  my $ticks = ($ms->Tempo() * 4)/$nref->[0];
  if ($nref->[3] == 1) {
   $ticks *= 1.5;
  }
  elsif ($nref->[3] == 2) {
   $ticks *= 1.75;
  }
  if ($nref->[1] eq 'P') {
   $ms->r("d$ticks");
  }
  else {
   my $note = uc($nref->[1]);
   $note =~ s/#/s/go;
   $ms->n("d$ticks",$note . $nref->[2]);
  }
 }
 my $midi;
 local(*OUT);
 tie *OUT, 'IO::String', \$midi;
 $ms->write_score(\*OUT);
 close(OUT);
 return $midi;
}
