sub indexes {
	my $obj = shift;
	my $table = shift;

	return () unless $table;

	my $d = $obj->{dbi}->selectall_arrayref(q!SELECT all_indexes.index_name,all_ind_columns.column_name FROM all_indexes,all_ind_columns WHERE all_indexes.owner = ? AND all_indexes.table_name = ? AND all_indexes.index_name = all_ind_columns.index_name!,{},$obj->{schema},$table);

	if (defined $d) {
		my %res = ();
		for (@$d) {
			push @{$res{$_->[0]}},$_->[1];
		}
		my @res = ();
		for (keys %res) {
			push @res,$res{$_};
		}
		return @res;
	}
	return ();
}

sub relationships {
	my $obj = shift;

	my $d = $obj->{dbi}->selectall_arrayref(q!SELECT st.relname,dt.relname,COALESCE(NULLIF(tgconstrname,'<unnamed>'),tgname),tgargs FROM pg_trigger,pg_class st,pg_class dt WHERE tgfoid = 1644 AND st.oid = tgrelid AND dt.oid = tgconstrrelid!);

	return map { 
		my @args = split /\x0/,$_->[3]; 
		my $f = 1;  my @cols1 = ();  my @cols2 = ();
		for (@args[4..$#args]) { 
			if ($f) { push @cols1,$_; $f = 0; } else { push @cols2,$_; $f = 1; } 
		}; 
		{ from_table => $_->[0], to_table => $_->[1], name => $_->[2], 
		  from_columns => [ map { { table => $args[1], column => $_ } } @cols1 ], 
		  to_columns => [ map { { table => $args[2], column => $_ } } @cols2 ] } 
	    } @$d if defined $d and @$d;
	return ();
}
