our @V = (
  V(
    'html',

    layout => sub {
      my ($self, $v, @content) = @_;
      html(
        head(
          title("unicode codepoints"),
          style($self->_css),
        ),
        body(
          x(@content),
        ),
      )->as_HTML;
    },

    _css => sub {qq|
      body {
        font-size: 10pt;
        background: $C->{bg};
        color: $C->{fg};
      }
      a {
        color: $C->{a};
        text-decoration: none;
      }
      a:hover {
        color: $C->{ah};
      }
      td {
        padding: 8px;
        width: 88px;
        font-family: monospace;
      }
      tr td:last-child {
        font-family: sans-serif;
      }
    |},

    home => sub {
      my ($self, $v) = @_;
      div(
        x($self->_pager($v)),
        table(
          map { 
            my $o = ord($_);
            &tr(
              td(sprintf('0x%04X', $o)),
              td(sprintf('&#x%04X;', $o)),
              td($_),
            ) 
          } @{$v->{chars}}
        ),
        x($self->_pager($v)),
      )->as_HTML;
    },

    _pager => sub {
      my ($self, $v) = @_;
      div(
        a({ href => R('Home', $v->{prev}) }, "<prev"),
        x(" | "),
        a({ href => R('Home', $v->{next}) }, "next>"),
      )->as_HTML;
    },

  ),
)
