sub outdent($;$)
{
	my $text = shift;
	my $opts = shift;
	my $re;
	if( (ref($opts)||'') eq 'Regexp' )
	{
		$re = $opts;
		$opts = {};
	}
	$re ||= $opts->{indent};
	if( !$re )
	{
		my ($len) = sort{ $a<=>$b } map{length($_)} $text =~ /^([ \t]*(?=\S))/gm;
		$re = $len ? qr/[ \t]{1,$len}/ : qr//;
	}
	xoutdent $text, {%$opts, indent=>$re, xchar=>'' };
}
