sub t9_find_words($$)
{
  my $num = shift;
  my $words = shift;

  return () unless $num =~ /^[2-9]+$/;

  my $len = length( $num );
  my $re;
  for( 0 .. $len - 1 )
    {
    $re .= "[" . $T9NL[substr( $num, $_, 1 )] . "]";
    }
  $re = "^$re\$";
  return grep { /$re/i } grep { length( $_ ) == $len } @$words;
}
