{
    my ( $warn, $fatal) = ( 0, 0);

    sub warnings
    {
        my (undef, $toggle) = @_;

        $toggle ||= 'on';
        if ( $toggle eq 'off' )
        {
            ($warn, $fatal) = (0, 0);
        }
        elsif ( $toggle eq 'fatal' )
        {
            ($warn, $fatal) = (1, 1);
        }
        else
        {
            ($warn, $fatal) = (1, 0);
        }
        return $fatal ? 'fatal' : $warn ? 'on' : 'off';
    }

    sub _warn
    {
        my $msg = shift;

        return unless $warn;

        if ($fatal)
        {
            croak( $msg)
        }

        carp( $msg);

        return;
    }
}
