sub getNodeContentsAsText {
  my $name       = shift; 
  my $ref        = shift; 
  my $nsref      = shift; 
  my %ns         = %{$nsref};
  my @array      = @{$ref};
  my %attributes = (%{ shift @array },%ns);
  my $str        = "<$name";
  if ( %attributes ) { foreach (sort keys %attributes) { $str.=" $_=\"$attributes{$_}\""; } }
  if (@array) {
    $str.=">";
    for(my $i=0;$i<@array;$i+=2) {
      if ($array[$i]) { $str.=getNodeContentsAsText($array[$i],$array[$i+1],$nsref); }
      else { $str.=$array[$i+1]; }
    }
    $str.="</$name>";
  }
  else { $str .= "/>"}
  return $str;
}
