sub _extract_fields
{
  my ($hdl, @types) = @_;

  my $i = $MsiRecordGetFieldCount->Call($hdl) or die $!;
  my @a = ();
  for my $c (1..$i)
  {
    my $s;
    if (@types)
    {
      my $t = shift @types;
      $s = $t == $COLTYPE_INT ? _getI($hdl, $c) :
         $t == $COLTYPE_STRING ? _getS($hdl, $c) :
         undef;          
    }
    else              
    {
      $s = _getI($hdl, $c);
      $s = _getS($hdl, $c) unless (defined($s));
    }
    push @a, $s;
  }
  return @a;
}
