sub _evdate {


   my ($self,$rss,$elt,$date) = @_;

	my $w3cdtf;

	if (ref $date eq 'HASH') { 
		my $dt = DateTime->new($date);
		my $f = DateTime::Format::W3CDTF->new;
		$w3cdtf = (exists $date->{'hour'} || exists $date->{'minute'} || exists $date->{'second'}) ? $f->format_datetime($dt) : $f->format_date($dt); 
	} elsif (ref $date eq 'DateTime') { 
		my $f = DateTime::Format::W3CDTF->new;
		$w3cdtf = $f->format_datetime($date);		
	} elsif ($date =~ /^[0-9]+$/) { 
		my $dt = DateTime->from_epoch( epoch => $date );
		my $f = DateTime::Format::W3CDTF->new;
		$w3cdtf = $f->format_datetime($dt);
	} else {
		$w3cdtf = $date;
	}

	$rss->_element_with_data('ev',$elt,$w3cdtf);

}
