sub substitute
{
    my ($string, $pattern, $replace) = @_;

    if ($string =~ /(.*)/) {
        $string = $1;
    }
    if ($pattern =~ /(.*)/) {
        $pattern = $1;
    }
    if ($replace =~ /(.*)/) {
        $replace = $1;
    }

    if ($pattern eq '') {
        ExtProc::ora_exception("substitute: empty pattern");
        return 0;
    }

    $string =~ s/$pattern/$replace/g;

