sub get_screen_width {
	my $self = shift;

	my $screen_width = $self->get_preference('screen_width');

	if ( not $screen_width ) {
		eval {
			require Term::ReadKey;
			($screen_width) = Term::ReadKey::GetTerminalSize();
		};
		if ( $@ ) {
			$self->info ("Warning: could not determine screen width! Set to 79.");
			$screen_width = 79;
		}
	}

	return $screen_width;
}
