sub send_and_expect
{
    my ($ofh, $ifh, $send, $expect, $debug) = @_;

    $debug |= 0; 

    print STDERR "> $send" if ($debug and $send !~ /user|pass/);
    print $ofh $send;

    my $line;
    while (defined($line = <$ifh>))
    {
        print STDERR "< $line" if $debug;
        return $line if $line =~ /$expect/;
    }

    undef;
}

sub error
{
    my $text = shift;

    $IMS::ReleaseMgr::FileList::error_text = $text
        if (defined $text and $text);

    $IMS::ReleaseMgr::FileList::error_text;
}

sub SIG_dec_trace
{
    my $sig = shift;

    $SIG{$sig} = \&SIG_dec_trace;

    my $cmd = ($main::cmd || $0);

    $cmd =~ s|.*/||o;
    if (defined $main::trace and $main::trace)
    {
        $main::trace--;
    }
    else
    {
        $main::trace = 0;
    }

    write_log_line(($main::tfile || "$0.trace"),
                   sprintf("$cmd [$$] [%s] Trace-level changed to $::trace",
                           scalar localtime time));

    1;
}

sub ACL_dir
{
    my $dir = shift;

    my $ret_val = $ACL_DIR;
    $ACL_DIR = $dir if (defined $dir and ! ref($dir));

    $ret_val;
}
