sub hidebox {
    my ($zinc, $self) = @_;
    my $ev = $zinc->XEvent();
    my $lx = $ev->x;
    my $ly = $ev->y;
    my @next;

    @next = $zinc->find('closest', $lx, $ly);
    if ((scalar(@next) == 0) ||
	! $zinc->hastag($next[0], 'currentbbox') ||
	$zinc->hastag('current', 'currentbbox')) {
	$zinc->itemconfigure('currentbbox', -visible => 0);
    }
}
